mapper.select:
SELECT COUNT(*)
FROM User_Portfolio_Like
WHERE user_pid = #{userPid}

SELECT p.id, p.title, p.thumbnail, p.like_count, p.created_at FROM Portfolio p
p.user_pid = #{userPid}
AND p.title LIKE CONCAT('%', #{keyword}, '%')
EXISTS ( SELECT 1 FROM Portfolio_Tags pt WHERE pt.portfolio_id = p.id AND pt.tag = #{tag} )
ORDER BY p.created_at DESC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM Portfolio p

    p.user_pid = #{userPid}


        AND p.title LIKE CONCAT('%', #{keyword}, '%')




            EXISTS (
            SELECT 1
            FROM Portfolio_Tags pt
            WHERE pt.portfolio_id = p.id
            AND pt.tag = #{tag}
            )

mapper.update:
UPDATE Portfolio
SET like_count = IFNULL(like_count, 0) + 1
WHERE id = #{portfolioId};

mapper.delete:
DELETE
FROM User_Portfolio_Like
WHERE user_pid = #{userPid}
  AND portfolio_id = #{portfolioId}

mapper.update:
UPDATE Portfolio
SET like_count = IF(like_count > 0, like_count - 1, 0)
WHERE id = #{portfolioId}
